mapper.select:
select twm.id id, twm.drug_id drugId,twm.warehouse_code warehouseCode,twm.drug_name drugName,twm.drug_alisa drugAlisa,
twm.expiration_time expirationTime,twm.insert_num insertNum,twm.residue_num residueNum,twm.create_time createTime,
twm.create_by createBy,twm.update_time updateTime, twm.update_by updateBy, su.user_name userName
from t_warehouse_manager twm
left join sys_user su on twm.create_by = su.user_id

    expire_status = '0'
     and twm.drug_id = #{drugId}
     and twm.warehouse_code like concat('%', #{warehouseCode}, '%')
     and (twm.drug_name like concat('%', #{drugName}, '%') or twm.drug_alisa like concat('%', #{drugName}, '%'))
     and twm.drug_alisa like concat('%', #{drugAlisa}, '%')
     and twm.expiration_time <= #{expirationTime}
     and twm.insert_num = #{insertNum}
     and twm.residue_num = #{residueNum}
     and twm.residue_num = #{residueNum}
     and twm.expiration_time between #{beginTime} and #{endTime}

order by twm.expiration_time

mapper.update:
update t_warehouse_manager
drug_id = #{drugId}, warehouse_code = #{warehouseCode}, drug_name = #{drugName}, drug_alisa = #{drugAlisa}, expiration_time = #{expirationTime}, insert_num = #{insertNum}, residue_num = #{residueNum}, create_time = #{createTime}, create_by = #{createBy}, update_time = #{updateTime}, update_by = #{updateBy}, expire_status = #{expireStatus}, is_delete = #{isDelete},
where id = #{id}
